
public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String a="1";
		System.out.println("String into int "+Integer.parseInt(a));
		System.out.println("String into double "+Double.parseDouble(a));
		System.out.println("String into boolean "+Boolean.parseBoolean(a));
		System.out.println("String into byte "+Byte.parseByte(a));
		System.out.println("String into long "+Long.parseLong(a));
		System.out.println("String into float "+Float.parseFloat(a));
	}

}
